mapper.select:

select no, title, contents, reg_date, category_no, user_id
  from post
 where no=#{id }


select no, title, contents, reg_date, category_no, user_id
  from post
 where user_id=#{id }


select no, title, contents, reg_date, category_no, user_id
  from post
 where user_id=#{id }
 and   category_no like #{c}
 order by reg_date desc
 limit #{p }, 5


select count(*) 
from   post 
where  user_id = #{user_id }
