mapper.select:

		SELECT no, title, contents, reg_date, category_no, user_id
		FROM   post
		WHERE  no          like #{no }
		AND    user_id     like #{user_id}
		AND    category_no like #{category_no} 
		ORDER  BY no desc
        LIMIT  1


select no, title, contents, reg_date, category_no, user_id
  from post
 where user_id=#{id }


